local inputListenerers = {}InputManager = {}function InputManager.addListender(l)	table.insert(inputListenerers, l)endfunction InputManager.removeListener(l)	for i, v in ipairs(inputListenerers) do		if l == v then			table.remove(inputListenerers, i)			break		end	endendfunction InputManager.KeyPressed(arg)	for _, v in ipairs(inputListenerers) do		v:keyPressed(arg)	endendfunction InputManager.KeyReleased(arg)	for _, v in ipairs(inputListenerers) do		v:keyReleased(arg)	endend